def first(instructions):
    return instructions.count("(") - instructions.count(")")


def second(instructions):
    pos = 0
    for i in range(len(instructions)):
        if instructions[i] == '(':
            pos += 1
        else:
            pos -= 1
        if pos == -1:
            return i + 1


